def load_data(dataDir):

  ################################################################################
  # Mounting Google Drive for accessing data files
  ################################################################################
  from google.colab import drive
  drive.mount('/content/drive')
  PurchaseDetails = os.path.join(dataDir, 'PurchaseDetails.xlsx')
  SalesQty = os.path.join(dataDir, 'SalesQty.xlsx')
  StoreStockQty = os.path.join(dataDir, 'StoresStockQty.xlsx')
  WarehouseStockAll  = os.path.join(dataDir, 'WarehouseStock.xlsx')
  MasterReplenishmentStock  = os.path.join(dataDir, 'MasterReplenishment&Stock.xlsx')
  Purchase  = pd.read_excel(PurchaseDetails)
  Sales  = pd.read_excel(SalesQty)
  StoresStock = pd.read_excel(StoreStockQty,sheet_name=['HO Less Stock', 'Packed Stock Store', 'Store Data'])
  StoresData = StoresStock['Store Data']
  PackedStoresData = StoresStock['Packed Stock Store']
  StoresData = pd.DataFrame(StoresData)
