def getSales(Sales):
  Sales.columns = Sales.iloc[1]
  Sales = Sales.iloc[2:]
  del Sales['Date']
  #Sales.head()
  SalesGroup = Sales.groupby(['Barcode','WarehouseID'])['Qty - Net Sales'].agg('sum')
  #SalesGroup.reset_index(inplace=True)
  SalesGroup = SalesGroup.reset_index()
  return SalesGroup


def getUniqueWarehouse(WarehouseStock):
  WarehouseStock.columns = WarehouseStock.iloc[1]
  WarehouseStock = WarehouseStock.iloc[2:].copy()  # Ensure you're working with a copy to avoid modifying the original DataFrame
  WarehouseStock[['Barcode','extra']] = WarehouseStock['Row Labels'].str.split(':', expand=True)
  WarehouseId = Sales['WarehouseID'].unique()
  WarehouseId = pd.DataFrame(WarehouseId)
  WarehouseId['WarehouseID'] = WarehouseId[0]
  del WarehouseId[0]
  return WarehouseId



def finalMerge(Purchase, Sales, StoresData, WarehouseStock):
  FinalMerge = pd.merge(pd.merge(pd.merge(Purchase, Sales, on='Barcode', how='inner'), StoresData, on='Barcode', how='inner'), WarehouseStock, on='Barcode', how='inner')
  FinalMerge = FinalMerge.drop_duplicates()
  FinalMerged = pd.DataFrame()
  FinalMerged['Hir1'] = FinalMerge['Hir1_x']
  FinalMerged['Hir2'] = FinalMerge['Hir2_x']
  FinalMerged['Hir3'] = FinalMerge['Hir3_x']
  FinalMerged['Product Details'] = FinalMerge['Product Details_x']
  FinalMerged['Barcode'] = FinalMerge['Barcode']
  FinalMerged =FinalMerged.drop_duplicates()
  return FinalMerged

def sumStoresData(StoresData):
  StoresDataNew = pd.DataFrame()
  #StoresDataNew['Total Stock'] = StoresData['Total Qty - Closing Stock']
  StoresDataNew['Transit Stock'] = StoresData['In-Transit Qty']
  StoresDataNew['Stock'] = StoresData['Qty - Closing Stock']

  StoresDataNew['Barcode'] = StoresData['Barcode']
  StoresDataNew['WarehouseID'] = StoresData['WarehouseID']
  StoresDataGroup = StoresDataNew.groupby(['Barcode','WarehouseID']).sum()
  StoresDataGroup.reset_index(inplace=True)

  return StoresDataGroup

def mergeStockSales(StoresDataNew,GrpSales):
  consStockSales = StoresDataNew.merge(GrpSales, on=['Barcode','WarehouseID'], how='left')
  return consStockSales

import numpy as np

def TODaysSales(consStockSales,WarehouseHOStock):
  #pick the first duplicate
  WarehouseHOStock = WarehouseHOStock.drop_duplicates(subset = ['Barcode'], keep = 'first')
  #columns name Ho Stock : rename it

  consStockWarehouse = pd.merge(consStockSales,WarehouseHOStock[['Barcode','Total Qty - Closing Stock']],on='Barcode', how='inner')
  consStockWarehouse.rename(columns={'Total Qty - Closing Stock': 'HO Stock'}, inplace=True)
  consStockWarehouse = consStockWarehouse[['WarehouseID','Barcode','Qty - Net Sales','HO Stock','Transit Stock','Total Stock','Packed Stock','Stock']] #change warehuseid to store Qty - Net Sales = 21dayssalt
  consStockWarehouse.rename(columns={'Qty - Net Sales': '21 Days Sales'}, inplace=True)
  return consStockWarehouse



