def InitialStock(consStockWarehouse, num ):

  consStockWarehouse['DMS1'] = consStockWarehouse['21 Days Sales']/21 #(1.19*60)-
  consStockWarehouse['nc'] = 0.05
  consStockWarehouse['is'] = 0.0
  consStockWarehouse['DMS'] = consStockWarehouse[['nc','DMS1']].max(axis=1)
  consStockWarehouse['Initial Stock1'] = ((consStockWarehouse['DMS'] * num) - consStockWarehouse['Total Stock']) #check bodmas
  consStockWarehouse['Initial Stock'] = consStockWarehouse[['is','Initial Stock1']].max(axis=1)
  #print(consStockWarehouse)
  del consStockWarehouse['Initial Stock1']
  del consStockWarehouse['DMS1']
  del consStockWarehouse['nc']
  del consStockWarehouse['is']

  return consStockWarehouse

def getDemand(consStockWarehouse):
    # Aggregate 21 Days Sales by Barcode
    consStockWarehouse1 = consStockWarehouse.groupby('Barcode')['21 Days Sales'].sum().reset_index()
    consStockWarehouse1.rename(columns={'21 Days Sales': '21daysSalesAggregate'}, inplace=True)

    # Merge the aggregated data back to the original DataFrame
    consStockWarehouse = consStockWarehouse.merge(consStockWarehouse1, on='Barcode', how='left')
    consStockWarehouse['21daysSalesAggregate'] = consStockWarehouse['21daysSalesAggregate'].fillna(0.01)

    # Replace zero values in 21daysSalesAggregate with a small value to avoid division by zero
    consStockWarehouse['21daysSalesAggregate'] = consStockWarehouse['21daysSalesAggregate'].replace(0, 0.01)

    # Calculate Demand% using np.where to handle division by zero
    consStockWarehouse['Demand%'] = np.where(
        consStockWarehouse['21daysSalesAggregate'] == 0,
        0,
        consStockWarehouse['21 Days Sales'] / consStockWarehouse['21daysSalesAggregate']
    )

    consStockWarehouse['Demand%'] = consStockWarehouse['Demand%'].replace(np.inf, 0)
    consStockWarehouse['Demand%'] = consStockWarehouse['Demand%'].clip(lower=0)

    return consStockWarehouse



def getTransferAdvise(ConsDemand):

  ConsDemand['TransferAdvise1'] = ConsDemand[('HO Stock')] * ConsDemand['Demand%'] #=IFERROR(MIN(O21,M21*P21),0)
  ConsDemand['TransferAdvise'] = ConsDemand[['Initial Stock','TransferAdvise1']].min(axis=1)
  #ConsDemand['is'] = 0.0
  #ConsDemand['TranseferAdvise'] = ConsDemand[['is','TransferAdvise2']].min(axis=1)
  del ConsDemand['TransferAdvise1']
  #del ConsDemand['TransferAdvise2']
  #del ConsDemand['is']

  return ConsDemand

def reorder(TA):
  cols = ['WarehouseID',	'Hir1',	'Hir2',	'Hir3',	'Product Details',	'Barcode',	'21 Days Sales' ,'Packed Stock','Total Stock','HO Stock','DMS']
  TA = TA[cols]
  TA.rename(columns={'WarehouseID': 'Store'}, inplace=True)
  #del TA['21daysSalesAggregate']
  return TA

